$.customFileInput=function(){$("input[type=file].custom-file-input").each(function(a,c){var e=$(this);
if(!e.prop("id")){e.prop("id","file_"+a)
}var d="浏览";
if(e.prop("title")){d=e.prop("title")
}e.wrap("<div class='file-input'></div>");
var b=$('<label for="'+e.prop("id")+'" class="btn">'+d+"</label>");
e.before(b);
b.append(e);
e.change(function(){b.next(".name").remove();
b.after('<span class="name">'+$(this).val()+"</span>")
})})};$(function(){$.customFileInput();
if(navigator.userAgent.indexOf("Firefox")>0){$(document).on("click","label",function(a){if(a.currentTarget===this&&a.target.nodeName!=="INPUT"){$(this.control).click()
}})}});